@app.route("/")
def main():
    randomlist = []
    counter = 0
    while counter < 6:
        rand = random.randrange(1, len(db.session.query(Teacher).all()))
        if rand not in randomlist:
            randomlist.append(rand)
            counter += 1
    names = []
    abouts = []
    ratings = []
    prices = []
    pictures = []
    for id in randomlist:
        teacher = db.session.query(Teacher).get(id)
        names.append(teacher.name)
        abouts.append(teacher.about)
        ratings.append(teacher.rating)
        prices.append(teacher.price)
        pictures.append(teacher.picture)

    output = render_template("index.html", randomlist=randomlist, size=len(randomlist),
                             ratings=ratings, prices=prices, abouts=abouts, names=names, pictures=pictures)
    return output


@app.route("/goals/<goal>")
def goal(goal):
    if goal == "travel":
        goal_query = db.session.query(Goals).filter(Goals.travel)
    if goal == "work":
        goal_query = db.session.query(Goals).filter(Goals.work)
    if goal == "study":
        goal_query = db.session.query(Goals).filter(Goals.study)
    if goal == "relocate":
        goal_query = db.session.query(Goals).filter(Goals.relocate)
    rating_query = db.session.query(Teacher).order_by(Teacher.rating.desc())
    sortedteacheridlist = []
    sortedgoallist = []

    for go in goal_query.all():
        sortedgoallist.append(go.teacher.id)
    for teacher in rating_query.all():
        if teacher.id in sortedgoallist:
            sortedteacheridlist.append(teacher.id)

    names = []
    abouts = []
    ratings = []
    prices = []
    pictures = []
    for id in sortedteacheridlist:
        teacher = db.session.query(Teacher).get(id)
        names.append(teacher.name)
        abouts.append(teacher.about)
        ratings.append(teacher.rating)
        prices.append(teacher.price)
        pictures.append(teacher.picture)

    output = render_template("goal.html", sortedgoallst=sortedteacheridlist, goal=data.goals[goal], ratings=ratings,
                             prices=prices, abouts=abouts, size=len(sortedteacheridlist), names=names,
                             pictures=pictures)
    return output


@app.route("/profiles/<id>")
def teacher(id):
    teacher = db.session.query(Teacher).get(id)
    goalslist = []
    if teacher.goals.travel:
        goalslist.append("travel")
    if teacher.goals.study:
        goalslist.append("study")
    if teacher.goals.relocate:
        goalslist.append("relocate")
    if teacher.goals.work:
        goalslist.append("work")

    contents = teacher.free
    output = render_template("profile.html", name=teacher.name, about=teacher.about,
                             rating=teacher.rating, price=teacher.price,
                             picture=teacher.picture, goals=data.goals, goalslist=goalslist,
                             days=contents, id=id)
    return output


@app.route("/request", methods=['GET', 'POST'])
def req():
    form = REQUEST()
    if form.validate_on_submit():
        neudb = Request(name=form.name.data, number=form.number.data, goal=form.goal.data, time=form.free_time.data)
        db.session.add(neudb)
        db.session.commit()
        return render_template('done.html')
    return render_template("request.html", form=form)


@app.route("/booking/<id>/<day>/<time>", methods=['GET', 'POST'])
def bking(id, day, time):
    form = USER()
    teacher = db.session.query(Teacher).get(id)
    if form.validate_on_submit():
        booking = Booking(name=form.name.data, number=form.number.data, day=day, time=time, teacher=teacher)
        db.session.add(booking)
        db.session.commit()
        return render_template('done.html')
    return render_template("booking.html", id=id, time=time, name=teacher.name,
                           picture=teacher.picture, day=data.days[day], daykey=day, form=form)


@app.errorhandler(500)
def page_not_found(error):
    return '<img src="data:image/png;base64," align="center" class="w-75" alt="">'


@app.errorhandler(404)
def page_not_found404(error):
    return '<img src="data:image/png;base64," align="center" class="w-75" alt="">'


if __name__ == '__main__':
    app.run()
